const courseActual="https://api.privatbank.ua/p24api/pubinfo?exchange&json&coursid=11";let arrayBuy=[],arraySale=[],arrayCurrency=[],firstCurrency=document.querySelectorAll(".currency-buy"),secondCurrency=document.querySelectorAll(".currency-sale"),nameValue=document.querySelectorAll(".name-value");async function getCourse(){try{let e=await fetch(courseActual);return getInfo((await e.json()).splice(0,3))}catch(e){console.log(e)}}function getInfo(e){e.forEach(e=>{arrayCurrency.push(e.ccy),arrayBuy.push(e.buy),arraySale.push(e.sale),addNameCurrency(arrayCurrency),showCurrency(arrayBuy,firstCurrency),showCurrency(arraySale,secondCurrency)})}function addNameCurrency(e){for(let r=0;r<e.length;r++)"RUR"===e[r]&&(e[r]="RUB"),nameValue[r].textContent=e[r]+"/UAH"}function showCurrency(e,r){for(let t=0;t<e.length;t++){let a=parseFloat(e[t]).toFixed(2);r[t].textContent=a}}function zeroFirstFormat(e){return e<10&&(e="0"+e),e}function getTime(){let e=new Date;return`${zeroFirstFormat(e.getDate())}.${zeroFirstFormat(e.getMonth())}.${zeroFirstFormat(e.getFullYear())} ${zeroFirstFormat(e.getHours())}:${zeroFirstFormat(e.getMinutes())}:${zeroFirstFormat(e.getSeconds())}`}getCourse(),setInterval((function(){document.querySelector(".header-time__actual").innerHTML=getTime()}),100);